<?phpinclude "config.php";// field ID (nanti implement TOKEN kalau tidak malas :3$type = $_GET["type"];$id = (int) $_GET["id"];$table = $type === "makanan" ? "tbl_makanan" : "tbl_minuman";$id_field = $type === "makanan" ? "id_makanan" : "id_minuman";$stmt = executeQuery($conn, "SELECT * FROM $table WHERE $id_field = ?", [    "i",    $id,]);$data = $stmt->get_result()->fetch_assoc();if (isset($_POST["submit"])) {    $nama = htmlspecialchars($_POST["nama"]);    $daerah = htmlspecialchars($_POST["daerah"]);    $keterangan = htmlspecialchars($_POST["keterangan"]);    $sql =        $type === "makanan"            ? "UPDATE tbl_makanan SET nama_makanan = ?, daerah_makanan = ?, keterangan = ? WHERE id_makanan = ?"            : "UPDATE tbl_minuman SET nama_minuman = ?, jenis_minuman = ?, keterangan = ? WHERE id_minuman = ?";    $params = ["sssi", $nama, $daerah, $keterangan, $id];    $stmt = executeQuery($conn, $sql, $params);    if ($stmt) {        echo "<script>alert('Data berhasil diupdate!'); window.location='index.php';</script>";    } else {        echo "<script>alert('Error updating data!');</script>";    }}?><!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title>Edit Data</title>    <link href="assets/css/bootstrap.min.css" rel="stylesheet"></head><body><div class="container mt-5">    <h2>Edit Data <?php echo ucfirst($type); ?></h2>    <form action="" method="POST">        <div class="mb-3">            <label for="nama" class="form-label">Nama <?php echo ucfirst(                $type            ); ?></label>            <input type="text" name="nama" class="form-control" value="<?php echo htmlspecialchars(                $data["nama_" . $type]            ); ?>" required>        </div>        <div class="mb-3">            <label for="daerah" class="form-label">Daerah / Jenis</label>            <input type="text" name="daerah" class="form-control" value="<?php echo htmlspecialchars(                $data[$type === "makanan" ? "daerah_makanan" : "jenis_minuman"]            ); ?>" required>        </div>        <div class="mb-3">            <label for="keterangan" class="form-label">Keterangan</label>            <input type="text" name="keterangan" class="form-control" value="<?php echo htmlspecialchars(                $data["keterangan"]            ); ?>">        </div>        <button type="submit" name="submit" class="btn btn-primary">Update</button>    </form></div><script src="assets/js/bootstrap.bundle.min.js"></script></body></html>